function fog3D_demo

    function surf_plot
        LL      = 3;
        [x,y]   = meshgrid(-LL:1/8:LL);
        z       = peaks(x,y);
        z       = z / max(z(:)) * LL;
        surf(x,y,z);
        
        shading interp
        % % lightangle(-45,30)
        %camlight('headlight')
        camlight('right')
        lighting gouraud
        colormap(gray);
        colormap(jet);

        axis equal;
        axis([-1,1,-1,1,-1,1]*LL);
        drawnow;
        box on;

        %axis off;
        set(gca, 'XTick',[], 'YTick',[], 'ZTick',[] );
        %         xlabel('x');
        %         ylabel('y');
        %         zlabel('z');
    end
    function logo2
        L = 40*membrane(1,25);
        %logoFig = figure('Color',[0 0 0]);
        logoFig = gcf;
        logoax = axes('CameraPosition', [-193.4013 -265.1546  220.4819],...
            'CameraTarget',[26 26 10], ...
            'CameraUpVector',[0 0 1], ...
            'CameraViewAngle',9.5, ...
            'DataAspectRatio', [1 1 .9],...
            'Position',[0 0 1 1], ...
            'Visible','off', ...
            'XLim',[1 51], ...
            'YLim',[1 51], ...
            'ZLim',[-13 40], ...
            'parent',logoFig);
        s = surface(L, ...
            'EdgeColor','none', ...
            'FaceColor',[0.9 0.2 0.2], ...
            'FaceLighting','phong', ...
            'AmbientStrength',0.3, ...
            'DiffuseStrength',0.6, ...
            'Clipping','off',...
            'BackFaceLighting','lit', ...
            'SpecularStrength',1, ...
            'SpecularColorReflectance',1, ...
            'SpecularExponent',7, ...
            'Tag','TheMathWorksLogo', ...
            'parent',logoax);
        l1 = light('Position',[40 100 20], ...
            'Style','local', ...
            'Color',[0 0.8 0.8], ...
            'parent',logoax);
        l2 = light('Position',[.5 -1 .4], ...
            'Color',[0.8 0.8 0], ...
            'parent',logoax);
    end


FogColor    = [1.0 1.0 1.0]*1.0;
figure('Color',FogColor, 'Units','Pixels', 'Position',[0 0 700 700]);
movegui(gcf,'west');
surf_plot
set( gca, 'CameraViewAngle',get(gca,'CameraViewAngle') );
fog3D(gca,'Add', 'Color',FogColor );

% pause(2);   fog3D(gca, 'hide');
% pause(2);   fog3D(gca, 'show');
% pause(2);   fog3D(gca, 'toggle');
% pause(2);   fog3D(gca, 'toggle');

FogColor    = [1.0 1.0 1.0]*1.0;
figure('Color',FogColor, 'Units','Pixels', 'Position',[0 0 700 700]);
movegui(gcf,'east');
logo2 %logo2 does not create new figure
fog3D(gca,'Add', 'Color',FogColor );



end